import React from "react";
import { Form, Button } from "react-bootstrap";
import { useFormik } from "formik";
import { formSchema } from "./schema";

export default function Loginpage() {
  const initialValues = {
    name: "",
    number: "",
    email: "",
    password: "",
    c_password: "",
  };
  const { values, handleSubmit, handleChange, handleBlur, errors } = useFormik({
    initialValues,
    validationSchema: formSchema,
    onSubmit: (values, actions) => {
      console.log(values);
      actions.resetForm();
    },
  });

  const exceptThisSymbols = ["e", "E", "+", "-", "."];

  return (
    <>
      <div className="container mt-3">
        <div className="row" style={{ marginTop: "10rem" }}>
          <div className="col-md-5">
            <h2 className="text-center">User Form</h2>
            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Name</Form.Label>

                <input
                  type="name"
                  placeholder="Enter Your Name"
                  className="form-control"
                  id="name"
                  name="name"
                  value={values.name}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
                {<p> {errors.name}</p>}
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label className="mt-3">Number</Form.Label>
                <input
                  onKeyDown={(e) =>
                    exceptThisSymbols.includes(e.key) && e.preventDefault()
                  }
                  type="Number"
                  placeholder="Enter Your Number"
                  className="form-control"
                  name="number"
                  id="number"
                  value={values.number}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label className="mt-3">Email </Form.Label>
                <input
                  type="email"
                  placeholder="Enter your  Email"
                  className="form-control"
                  name="email"
                  id="email"
                  value={values.email}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label className="mt-3"> Password</Form.Label>
                <input
                  className="form-control"
                  type="password"
                  placeholder="Password"
                  name="password"
                  id="password"
                  value={values.password}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
              </Form.Group>

              <Form.Group className="mb-3 " controlId="formBasicPassword">
                <Form.Label className="mt-3"> Confirm Password</Form.Label>
                <input
                  className="form-control"
                  type="password"
                  placeholder="Confirm  Your Password"
                  name="c_password"
                  id="c_password"
                  value={values.c_password}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
              </Form.Group>

              <Button
                className="mt-3"
                size="lg"
                variant="primary"
                type="submit"
              >
                Submit
              </Button>
            </Form>
          </div>
          <div className="col-md-7 my-auto">
            <img className="img-fluid w-100" src="./assets/rocket.png" alt="" />
          </div>
        </div>
      </div>
    </>
  );
}
