import { useState } from "react";
import { Form, Button } from "react-bootstrap";
import { useFormik } from "formik";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Loginpage() {
  const navigate = useNavigate();
  const initialValues = {
    email: "",
    password: "",
  };

  const { values, handleSubmit, handleChange, handleBlur } = useFormik({
    initialValues,

    onSubmit: (values) => {
      console.log(values);
      let url = "http://localhost:4000/login";
      axios.post(url, values).then(function (res) {
        if (res.data.success) {
          localStorage.setItem("x-access-token", res.data.token);

          // console.log(res.data.tokan);

          alert(res.data.message);
          navigate("/");
        } else {
          alert(res.data.message);
        }
      });
    },
  });

  const [show, setShow] = useState(false);

  const showpassword = () => {
    setShow(!show);
  };
  return (
    <div className="container mt-3">
      <div className="row" style={{ marginTop: "10rem" }}>
        <div className="col-md-5">
          <h2 className="text-center">Sign Up</h2>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label className="mt-3">Email </Form.Label>
              <input
                type="email"
                placeholder="Enter your  Email"
                className="form-control"
                name="email"
                id="email"
                value={values.email}
                onChange={handleChange}
                onBlur={handleBlur}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label className="mt-3">Password</Form.Label>

              <input
                className="form-control"
                type={show ? "text" : "password"}
                placeholder="Password"
                name="password"
                id="password"
                value={values.password}
                onChange={handleChange}
                onBlur={handleBlur}
              />

              <Form.Group className="mt-1" controlId="formBasicCheckbox">
                <Form.Check
                  type="checkbox"
                  label="Click Here to view Password"
                  onClick={showpassword}
                />
              </Form.Group>
            </Form.Group>

            <Button className="mt-4" size="lg" variant="primary" type="submit">
              Submit
            </Button>
          </Form>
          <div className="register">
            <p>Don't have an account? </p>
          </div>
        </div>
        <div className="col-md-7 my-auto">
          <img className="img-fluid w-100" src="./assets/rocket.png" alt="" />
        </div>
      </div>
    </div>
  );
}
